var vkontakte = require('vkontakte');
var Response = require('Response');

function ResponseVK (token) {
    this.token = token;
    this._vk = vkontakte(token);
}

module.exports = ResponseVK;

ResponseVK.prototype.send = function (method, params) {
    var r = new Response();

    r.invoke(this._vk, [method, params, this._callback.bind(r)], this._vk);

    return r;
};

ResponseVK.prototype._callback = function (error, data) {
    if (error) {
        this.reject(JSON.stringify(error));
    } else {
        this.resolve(data);
    }
};
